def groovyutils = new com.eviware.soapui.support.GroovyUtils(context) def path = groovyutils.projectPath Domibus.deleteFiles(log, path, ".jks")
Domibus.generateKeyStore(context, log, workingDirectory, keystoreAlias, keystorePassword, privateKeyPassword) Domibus.generateKeyStore(context, log, workingDirectory, "purple_gw", keystorePassword, privateKeyPassword, 2, keystoreFileName) Domibus.generateKeyStore(context, log, workingDirectory, "red_gw", keystorePassword, privateKeyPassword, -2, keystoreFileName) Domibus.generateKeyStore(context, log, workingDirectory, "yellow_gw", keystorePassword, privateKeyPassword, 0, keystoreFileName)
Domibus.updateTrustStore(context, log, workingDirectory, keystoreAlias, keystorePassword, privateKeyPassword, keystoreFileName, truststoreFileName) Domibus.updateTrustStore(context, log, workingDirectory, "red_gw",, keystorePassword, privateKeyPassword, keystoreFileName) Domibus.updateTrustStore(context, log, workingDirectory, "yellow_gw", keystorePassword, privateKeyPassword, keystoreFileName) Domibus.updateTrustStore(context, log, workingDirectory, "purple_gw", keystorePassword, privateKeyPassword, keystoreFileName)
Domibus.deleteFiles(log, path, ".pem")

//Create iminent certificate def workingDirectory = context.expand('${#Project#tempFilesDir}') def blueKeystoreFileName = "aa1_blue_gateway_keystore.jks" def blueTruststoreFileName = "aa1_blue_gateway_truststore.jks" def redKeystoreFileName = "aa1_red_gateway_keystore.jks" def redTruststoreFileName = "aa1_red_gateway_truststore.jks"
