Domibus.generateKeyStore(context, log, workingDirectory, "blue_gw2", "test123", "test123", 2, blueKeystoreFileName) Domibus.generateKeyStore(context, log, workingDirectory, "red_gw", "test123", "test123", 100, redKeystoreFileName) Domibus.updateTrustStore(context, log, workingDirectory, "blue_gw2", "test123", "test123", blueKeystoreFileName, redTruststoreFileName) Domibus.updateTrustStore(context, log, workingDirectory, "red_gw", "test123", "test123", redKeystoreFileName, blueTruststoreFileName)
// Copy keystore blue def source = workingDirectory + "\\" + blueKeystoreFileName def dest = context.expand('${#Project#pathBlue}') +"conf\\domibus\\keystores\\" + blueKeystoreFileName Domibus.copyFile(source, dest, log)
// Copy keystore red expired source = workingDirectory + "\\" + redKeystoreFileName dest = context.expand('${#Project#pathRed}') +"conf\\domibus\\keystores\\" + redKeystoreFileName Domibus.copyFile(source,dest,log)
