con:config.script:
def ebsiTools = new eu.domibus.test.ebsi.EbsiTools(log,context);
def messageId = testRunner.testCase.getPropertyValue("UserMessageId");
def ebMSHeaderSend = ebsiTools.getMessageSignatureXML("C2", messageId);
def ebMSHeaderResponse = ebsiTools.getReferenceMessageSignatureXML("C2", messageId);
// asser ebms soap response  exists
log.info "--------------------------------------------------";
log.info "Request ebMS header: " + ebMSHeaderSend;
log.info "--------------------------------------------------";
log.info "Response ebMS header: " + ebMSHeaderResponse;
assert ebMSHeaderSend!=null;

//  -------------------------------
// Validate user messge
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
def holder = groovyUtils.getXmlHolder( ebMSHeaderSend );
def ebsiTimeStamp  = holder.getDomNode("//*:EBSITimeStamp");
def ebsiTimeStampUrl  = holder.getNodeValue("//*:TimeStampVerificationUrl");
def ebsiTimeStampTransaction  =  holder.getNodeValue("//*:TransactionHash");;

log.info "Timestamp: " + ebsiTimeStamp;
log.info "TimeStampUrl: " + ebsiTimeStampUrl;
log.info "TransactionHash: " + ebsiTimeStampTransaction;

assert ebsiTimeStamp!=null, "ERROR: Missing EBSITimeStamp in message";
assert ebsiTimeStampUrl!=null,"ERROR: Missing TimeStampVerificationUrl in message";
assert ebsiTimeStampTransaction==null,"ERROR: TransactionHash in message must be null";



// -------------------------------
// Validate signal response 
def holderResponse = groovyUtils.getXmlHolder( ebMSHeaderResponse );
def ebsiTimeStampResponse  = holderResponse.getDomNode("//*:EBSITimeStamp");
def ebsiTimeStampUrlResponse  = holderResponse.getNodeValue("//*:TimeStampVerificationUrl");
def ebsiTimeStampTransactionResponse  =  holderResponse.getNodeValue("//*:TransactionHash");;

log.info "Response Timestamp: " + ebsiTimeStampResponse;
log.info "Response TimeStampUrl: " + ebsiTimeStampUrlResponse;
log.info "Response TransactionHash: " + ebsiTimeStampTransactionResponse;

assert ebsiTimeStampResponse==null, "ERROR:Response EBSITimeStamp in message must be null";
assert ebsiTimeStampUrlResponse==null,"ERROR: Response TimeStampVerificationUrl in message must be null";
assert ebsiTimeStampTransactionResponse==null,"ERROR: Response TransactionHash in message must be null";

